import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import React, { useState } from 'react'
import { Navigate, Link } from 'react-router-dom'

function NavScroll() {
  const [search, setSearch] = useState('')

  const handleLogout = () => {
    localStorage.removeItem('token')

    navigate("/login");
  }
  return (
    <Navbar bg="light" expand="lg">
      <Container fluid>

        <Link className='navbar-brand fs-1' to="/">Online Food Project</Link>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse>

          <Nav className="me-auto my-2 my-lg-0"
            style={{ maxHeight: '100px' }} navbarScroll>

            <Link className='nav-link active' aria-current="page" to="/">Home</Link>


          </Nav>

          {(localStorage.getItem("token")) ?
            <li className="nav-item">
              <Link className="nav-link fs-5 mx-3 active" aria-current="page" to="/">My Orders</Link>  {/* index.css - nav-link color white */}
            </li> : ""}

          {(!localStorage.getItem("token")) ?
            <div className="d-flex">
              <Link className="btn bg-white text-success mx-1 " to="/login">Login</Link>
              <Link className="btn bg-white text-success mx-1" to="/signup">Signup</Link>
            </div>
            : <div>
              <div className="btn bg-white text-success mx-1">My Cart</div>
              <div className="btn bg-white text-success mx-1" onClick={handleLogout}>Logout</div>

            </div>
          }

        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavScroll;
