import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { Link} from 'react-router-dom';
import React ,{ useState }from 'react'

function NavScroll() {
  const [foodCat, setFoodCat] = useState([])
  const [foodItems, setFoodItems] = useState([])
  const [search,setSearch] = useState('')
  return (
    <Navbar bg="light" expand="lg">
      <Container fluid>
        
        <Link className='navbar-brand fs-1'to="/">Online Food Project</Link>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse>

          <Nav className="me-auto my-2 my-lg-0"
          style={{ maxHeight: '100px' }}navbarScroll>
            
            <Link className='nav-link active' aria-current="page" to="/">Home</Link>
            <Link className='nav-link active' aria-current="page" to="/Login">Login</Link>
            <Link className='nav-link active' aria-current="page" to="/SignUp">SignUp</Link>

          </Nav>
          <Form className="d-flex">
            <Form.Control type="search" placeholder="Search" className="me-2" aria-label="Search"/>
            <Button variant="outline-success" value={search} onClick={(e) => { setSearch('e.target.value') }}>Search</Button>
          </Form>

          {foodItems !== [] ? foodItems.filter(
                    (items) => (items.CategoryName === data.CategoryName) && (items.name.toLowerCase().includes(search.toLowerCase())))
                    .map(filterItems => {
                      return (
                        <div key={filterItems.id} className='col-12 col-md-6 col-lg-3'>
                          {console.log(filterItems.url)}
                          <Card foodName={filterItems.name} options={filterItems.options[0]} ImgSrc={filterItems.img} ></Card>
                        </div>
                      )
                    }) : <div> No Such Data </div>}

        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavScroll;
