import React from 'react';
import Card from 'react-bootstrap/Card';


export default function Body(props) {
    let options = props.option;
    let priceOptions = Object.keys(options)
    return (
        <>

<div>

            <Card className='mt-3' style={{ width: '18rem',maxHeight:'360px'}}>
                <Card.Img variant="top" src="2.jpg" className='card-img-top' alt='img' />
                <Card.Body>
                    <Card.Title>{props.foodName}</Card.Title>
                    <Card.Text>
                        Some quick example text to.
                    </Card.Text>
                    <div className='container'>
                        <select>
                            {
                                Array.from(Array(6),(e,i)=>{
                                    return(
                                        <option key={i+1} value={i+1}> {i+1} </option>
                                        )   
                                })
                            }
                        </select>
                        <select className='rounded m-2'>
                            {priceOptions.map((data)=>{
                                return <option key={data} value={data}>{data}</option>
                            }
                            )}
                        </select>
                        <div className='d-inline fs-5'>
                            Total Price
                        </div>
                    </div>
                 
                </Card.Body>
            </Card>

            

        </div>
        </>    
        )
}
