import React,{useEffect, useRef, useState} from 'react';
import Card from 'react-bootstrap/Card';
import { useDispatchCart, useCart } from './ContextReducer';

export default function Body(props) {
    let options = props.options;
    let priceOptions = Object.keys(options);

    let dispatch = useDispatchCart();
    
    let data = useCart();

    const priceRef = useRef();

    const [qty,setQty] = useState(1)
    const [size,setSize] = useState("")

    const handleAddToCart = async ()=>{
        
            await dispatch({type:"ADD",id:props.foodItem._id,name:props.foodItem.name,price:props.finalPrice, qty:qty, size:size})
            console.log(dispatch)
    }

    let finalPrice = qty * parseInt(options[size]);
    useEffect(() =>{
        setSize(priceRef.current.value)
    },[]) 

    return (
        <>

            <div>

                <Card className='mt-3' style={{ width: '18rem', maxHeight: '500px' }} >
                    <Card.Img src={props.foodItem.img} className='card-img-top' alt='img' style={{ height: "150px", objectFit: "fill"}} />
                    <Card.Body>
                        <Card.Title>{props.foodItem.CategoryName}</Card.Title>
                        <Card.Title>{props.foodItem.name}</Card.Title>
                        <Card.Text>{props.foodItem.description}</Card.Text>
                        <div className='container'>
                            <select onChange={(e) => setQty(e.target.value)}>
                                {
                                    Array.from(Array(6), (e, i) => {
                                        return (
                                            <option key={i + 1} value={i + 1}> {i + 1} </option>
                                        )
                                    })
                                }
                            </select>

                            <select className='rounded m-2' ref={priceRef} onChange={(e) => setSize(e.target.value)} >
                                {priceOptions.map((option) => (
                                    <option key={option} value={option}>{option}</option>
                                ))}
                            </select>

                            {/* <select className='rounded m-2' ref={priceRef} onChange={(e) => setSize(e.target.value)} >
                                {priceOptions.map((i) => {
                                    return <option key={data} value={data}>{data}</option>
                                })}
                            </select> */}
                            
                            <div className='d-inline fs-5'>
                                ${finalPrice}/-
                            </div>
                        </div>
                                <hr/>
                                <button className={'btn btn-success justify-center ms-2'} onClick={handleAddToCart}>Add to Cart</button>
                    </Card.Body>
                </Card>



            </div>
        </>
    )
}
